import React from "react";
import graphql from "babel-plugin-relay/macro";
import {
  RelayEnvironmentProvider,
  loadQuery,
  usePreloadedQuery,
} from "react-relay/hooks";
import env from "../RelayEnvironment";
import {
  Button,
  Container,
  Box,
  Typography,
  Card,
  CardContent,
  CardActions,
  Link,
  TextField,
  InputAdornment,
  Backdrop,
  CircularProgress
} from "@mui/material";

const { Suspense } = React;

const RepositoryNameQuery = graphql`
  query HomeRepositoryNameQuery($usr: String!) {
    user(login: $usr) {
      repositories(first: 10, orderBy: { field: CREATED_AT, direction: DESC }) {
        nodes {
          name
          description
          collaborators(first: 10) {
            nodes {
              name
            }
          }
          issues(first: 10, orderBy: { field: CREATED_AT, direction: DESC }) {
            nodes {
              __typename
            }
          }
          createdAt
          url
        }
      }
    }
  }
`;

const preloadedQuery = loadQuery(env, RepositoryNameQuery, {
  usr: localStorage.getItem("user"),
});

function Home(props) {
  const data = usePreloadedQuery(RepositoryNameQuery, props.preloadedQuery);

  const signOut = () => {
    localStorage.clear();
    window.location = "/";
  };

  return (
    <Container>
      {data ? (
        <Box>
          <Typography
            color="lightgrey"
            my={10}
            sx={{ fontSize: 72, fontWeight: 900 }}
          >
            Repo-improv
          </Typography>
          <Button variant="contained" onClick={signOut}>
            Sign Out
          </Button>
          <Box
            sx={{
              display: "flex",
              flexDirection: "row",
              flexWrap: "wrap",
              justifyContent: "space-between",
              alignItems: "stretch",
            }}
          >
            {data.user.repositories.nodes.map((data) => {
              return (
                <Card
                  sx={{
                    minWidth: 275,
                    margin: 5,
                    display: "flex",
                    flexDirection: "column",
                    flexWrap: "wrap",
                  }}
                >
                  <CardContent>
                    <Typography
                      sx={{ fontSize: 14 }}
                      color="text.secondary"
                      gutterBottom
                    >
                      {data.createdAt}
                    </Typography>
                    <Typography variant="h5" component="div">
                      {data.name}
                    </Typography>
                    <Typography sx={{ mb: 1.5 }} color="text.secondary">
                    <br/>
                      {data.description ? data.description : "no description"}
                    </Typography>
                    <Typography variant="body2">
                      {data.collaborators}
                    </Typography>
                  </CardContent>
                  <CardActions>
                    <Button>
                      <Link href={data.url}>link</Link>
                    </Button>
                  </CardActions>

                  <TextField
                    id="outlined-basic"
                    label="Rename app"
                    variant="outlined"
                    InputProps={{
                      endAdornment: (
                        <InputAdornment>
                          <Button>Go</Button>
                        </InputAdornment>
                      ),
                    }}
                  />
                </Card>
              );
            })}
          </Box>
        </Box>
      ) : (
        <h1>no data found</h1>
      )}
    </Container>
  );
}

function HomeRoot(props) {
  const [open, setOpen] = React.useState(false);

  const fallback = () => {
  const handleClose = () => {
    setOpen(false);
  };
  const handleToggle = () => {
    setOpen(!open);
  };
    return(
      <>
      <Button onClick={handleToggle}>Show backdrop</Button>
<Backdrop
  sx={{ color: '#fff', zIndex: (theme) => theme.zIndex.drawer + 1 }}
  open={open}
  onClick={handleClose}
>
  <CircularProgress color="inherit" />
</Backdrop>
</>
    )
  }

  return (
    <RelayEnvironmentProvider environment={env}>
      <Suspense fallback={"Loading..."}>
        <Home preloadedQuery={preloadedQuery} />
      </Suspense>
    </RelayEnvironmentProvider>
  );
}

export default HomeRoot;
